mapper.select:
SELECT id, username, nickname, password, create_time, role_type
FROM administrator
WHERE id = #{administratorId}

SELECT id, username, nickname, password, create_time, role_type
FROM administrator
WHERE username = #{username}

mapper.update:
UPDATE books

    username=#{username},
    password=#{password},
    nickname=#{nickname},
    roletype=#{roletype}

WHERE id = #{id}
